#!/usr/bin/env node

const { spawn } = require('child_process')

const player = require('play-sound')(opts = {})
 
// $ mplayer foo.mp3 
const audio = player.play('allstar.mp3', function(err){
})

const [node, index, ...args] = process.argv
const childProcess = spawn('npx', ['create-react-app'].concat(args), {
  stdio: 'inherit'
})

childProcess.on('exit', () => { 
  kill()
})
childProcess.on('error', err => console.log(err))

const kill = () => audio.kill()

process.on('beforeExit', () => kill())

